import React, { useEffect } from 'react'
import { Tooltip, IconButton, TextField, Dialog, Zoom } from '@material-ui/core'
import AddIcon from '@material-ui/icons/Add';
import CreateIcon from '@material-ui/icons/Create';
import Carousel from 'react-material-ui-carousel'


export default class Dashboard extends React.Component {
    constructor(props) {
        super(props);

        this.state = {
            userDetails: {
                username: "",
                email: ""
            },
            createWordlistOpen: false
        }
    }

    // Adding user details pre render
    componentWillMount() {
        let uid = sessionStorage.getItem("uid");
        fetch('http://sddmajordev:5000/api/userbase/get_user_by_uid', {
            method: 'post',
            headers: {
              'Content-Type':  'application/json',
            }, 
            body: JSON.stringify({"uid": uid})
          })
          .then(response => response.json())
          .then(data => {
              this.setState({userDetails: data})
        })
    }

    render() {
        var items = [
            {
                name: "Random Name #1",
                description: "Probably the most random thing you have ever seen!"
            },
            {
                name: "Random Name #2",
                description: "Hello World!"
            }
        ]

        if (this.state.accountType === "student") {
            // Return Student Dashboard
        } else {
            // Return Teacher Dashboard
            return (
                <div>
                    <Tooltip title="Add Student" TransitionComponent={Zoom} arrow>
                        <IconButton aria-label="delete" style={{backgroundColor: "#FF7979", float: "right"}}>
                            <AddIcon style={{color: "white"}}/>
                        </IconButton> 
                    </Tooltip>
                    <Tooltip title="Create New Wordlist" TransitionComponent={Zoom} style={{padding: "2%"}}arrow>
                        <IconButton aria-label="delete" style={{backgroundColor: "#FF7979", float: "right"}}>
                            <CreateIcon style={{color: "white"}}/>
                        </IconButton> 
                    </Tooltip>
                    <h1 style={{textAlign: "center"}}>Hello, {this.state.userDetails.username}</h1>
                    <div style={{display: "inline-block", padding: "3%"}}>
                        <Tooltip title="Create New Wordlist" TransitionComponent={Zoom} arrow>
                            <IconButton aria-label="delete" style={{backgroundColor: "#FF7979"}} onClick={this.handleClick}>
                                <AddIcon style={{width: "100", height: "100", color: "white"}}></AddIcon>
                            </IconButton>
                        </Tooltip>
                        <IconButton aria-label="delete">
                            <AddIcon style={{width: "100", height: "100"}}></AddIcon>
                        </IconButton>
                    </div>
                    <TextField label="Search Wordlist" variant="outlined"></TextField>
                </div>
            )
        }
    }
}
